import { useEffect, useState } from "react";
import { getAnswersByUserId } from "../../services/answersSevied";
import { getListTopic } from "../../services/topicServies";

function Answers() {
    const [dataAnswers, setDataAnswers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchApi = async () => {
            try {
                const answersByUserId = await getAnswersByUserId();
                const topics = await getListTopic();
                console.log(topics);
                console.log(answersByUserId);

                let result = answersByUserId.map(answer => ({
                    ...topics.find(item => item.id === answer.topicId),
                    ...answer,
                }));

                setDataAnswers(result);
            } catch (err) {
                setError("Có lỗi xảy ra khi tải dữ liệu");
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchApi();
    }, []);

    if (loading) return <p>Đang tải dữ liệu...</p>;
    if (error) return <p>{error}</p>;

    return (
        <>
            <h1>Danh sách bài luyện tập</h1>
            <ul>
                {dataAnswers.map((answer, index) => (
                    <li key={index}>
                        {answer.fullName} - {answer.topicName}
                    </li>
                ))}
            </ul>
        </>
    );
}

export default Answers;
