        <>
        <h2>Danh sách chủ đề </h2>
        <table className="table">
                    <thead className="table__head">
                        <tr >
                            <th>ID</th>
                            <th >Tên chủ đề </th>
                        </tr>
                    </thead>
                    <tbody >
                       <tr>
                        <td>1</td>
