import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { getTopic } from "../../services/topicServies";
import { getListQuestion } from "../../services/questionServies";
import { getCookie } from "../../helpers/cookie";
import { createAnswers } from "../../services/quizServices";

function Quiz() {
    const params = useParams();
    const [dataTopic, setDataTopic] = useState();
    const [dataQuestion, setDataQuestion] = useState([]);
    const userId = getCookie("id");
    const navigate = useNavigate();

    useEffect(() => {
        const fetchApi = async () => {
            const response = await getTopic(params.id);
            setDataTopic(response);
        };
        fetchApi();
    }, [params.id]);

    useEffect(() => {
        const fetchApi = async () => {
            const response = await getListQuestion(params.id);
            setDataQuestion(response);
        };
        fetchApi();
    }, [params.id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        let selectedAnswers = [];
        for (let i = 0; i < e.target.elements.length; i++) {
            if (e.target.elements[i].checked) {
                const name = e.target.elements[i].name;
                const value = e.target.elements[i].value;
                selectedAnswers.push({
                    questionId: parseInt(name),
                    answer: parseInt(value)
                });
            }
        }

        let option = {
            userId: parseInt(userId),
            topicsId: parseInt(params.id),
            answers: selectedAnswers
        };

        try {
            const response = await createAnswers(option);
            if (response) {
                navigate(`/result/${response.id}`);
            }
        } catch (error) {
            console.error("Failed to submit answers:", error);
        }
    };

    return (
        <>
            <h1>Bài Quiz chủ đề: {dataTopic && (<>{dataTopic.name}</>)}</h1>
            <div className="form-quiz">
                <form onSubmit={handleSubmit}>
                    {dataQuestion.map((item, index) => (
                        <div key={item.id}>
                            <p>Câu {index + 1}: {item.question}</p>
                            {item.answer.map((itemAns, indexAns) => (
                                <div className="form-quiz__answer" key={indexAns}>
                                    <input
                                        type="radio"
                                        name={item.id}
                                        value={indexAns}
                                        id={`quiz${item.id}${indexAns}`}
                                    />
                                    <label htmlFor={`quiz${item.id}${indexAns}`}>{itemAns}</label>
                                </div>
                            ))}
                        </div>
                    ))}
                    <button type="submit">Nộp bài</button>
                </form>
            </div>
        </>
    );
}
export default Quiz;
