import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"
import { getAnswers } from "../../services/answersSevied";
import { getListQuestion } from "../../services/questionServies";
import "./Result.css"

function Result() {
    const params = useParams();
    const [dataResult, setDataResult] = useState([]);

    useEffect(() => {
        const fecthApi = async () => {
            const dataAnswers = await getAnswers(params.id);
            const dataQuestion = await getListQuestion(dataAnswers.topicsId);
            

            let resultFinal = [];

            for (let i = 0; i < dataQuestion.length; i++) {
                resultFinal.push({
                    ...dataQuestion[i],
                    ...dataAnswers.answers.find(item => item.questionId == dataQuestion[i].id),
                });
            }
            console.log(resultFinal);
            setDataResult(resultFinal);
        };
        fecthApi();
    }, []);
    return (
        <>
            <h1>Kết quả:</h1>
            <div className="answer__list">
                {dataResult.map((item, index) => (
                    <div className="" key={item.id}>
                        <p>Câu {index + 1}: {item.question}
                            {item.correctAnswer === item.answer ? (
                                <span className="result__tag--true">Đúng</span>
                            ) : (<span className="result__tag--false">sai</span>)}
                        </p>

                        {item.answers.map((itemAns, indexAns) => {
                            let className="";
                            let checked = false;
                            if(item.correctAnswer === indexAns){
                                checked=true;
                                className = "result__item--selected";
                            }
                            if(item.correctAnswer === indexAns){
                                className += " result__item--result";
                            }
                            return (
                                <div className="result__answer" key={indexAns}>
                                    <input type="radio" checked={checked} disabled/>
                                    <label className={className} >{itemAns}</label>

                                </div>
                            )
                        })}
                    </div>
                ))}
            </div>
        </>
    )
}
export default Result
