import {useEffect, useState} from "react"
import {getAnswersByUserId} from "../../services/answersSevied"
import {getListTopic} from "../../services/topicServies"
import { Link } from "react-router-dom";
function Answers(){
    const [dataAnswers,setDataAnswers] =useState([]);
    useEffect( ()=>{
        const fecthApi = async () => {
            const AnswersByUserId = await getAnswersByUserId();
            const topics = await getListTopic();

            let result = [];

            for(let i = 0; i< AnswersByUserId.length;i++){
                result .push({
                    ...topics.find(item => item.id === AnswersByUserId[i].topicsId),
                    ...AnswersByUserId[i],
                   
                })
            }
           console.log(result);
           setDataAnswers(result.reverse());
        }
        fecthApi();
    },[]);
    console.log(dataAnswers);
    return(
        <>
        <h1>Danh sách bài luyện tập</h1>
        {dataAnswers.length > 0 && (
         <table className="table">
         <thead className="table__head">
             <tr >
                 <th>ID</th>
                 <th >Tên chủ đề </th>
             </tr>
         </thead>
         <tbody >
            {dataAnswers.map(item => (
                <tr key={item .id}>
                <td>{item .id}</td>
                <td>{item .name}</td>
                <td><Link to={"/quiz/"+item .id}>Xem chi tieets</Link></td>
               </tr>
            ))}
         </tbody>
     </table>
       )}
        </>
    )
}
export default Answers
