import { useNavigate } from "react-router-dom"
import {  checkExits, register } from "../../services/userServies";
import { useDispatch } from "react-redux"

import {generateToken} from "../../helpers/generateToken"
function Register() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
   
    const handleSubmit = async (e) => {
        e.preventDefault();
        const fullName = e.target[0].value;
        const email = e.target[1].value;
        const password = e.target[2].value;

        const checkExitEmail = await checkExits("email",email);
        console.log(checkExitEmail)
        if (checkExitEmail.length >0) {
            alert("Email dã tồn tại");
        }else{
            const options = {
            fullName: fullName,
            email: email,
            password: password,
            token:generateToken(),
        }
        const response = await register(options);
        if(response){
            navigate("/login",response.email)
        }else{
            alert("Sai tài khoản hoặc mật khẩu ")
        }
        }
      
      
    }
    return (

        <>
            <form onSubmit={handleSubmit}>
                <h2>Register</h2>
                <div>
                    <input type="fullname" placeholder="Nhập Họ Tên" />
                </div>
                <div>
                    <input type="email" placeholder="Nhập Email" />
                </div>
                <div>
                    <input type="password" placeholder="Nhập Mật khẩu" />
                </div>
                <button type="submit">
                    Login
                </button>
            </form>
        </>
    )
}
export default Register
